    public int estableceNotaEnEscala(String laEscala, int laNotaGenerada)
    {
        //System.out.print("Escala: " + laEscala + " Nota fractal: " + laNotaGenerada + " ");
        skala = laEscala;
        kualEscala = new RuleVariable(escalas, "kualEscala");
        notaFractal = new RuleVariable(escalas, "notaFractal");
        notaMidi = new RuleVariable(escalas, "notaMidi");
        if(laEscala.equals("GBlues") | laEscala.equals("CBlues") | laEscala.equals("CMelMinor") | laEscala.equals("BMelMinor"))
        {
            inicializaReglasBlues();
        }
        else
        {
            inicializaBaseDeReglas();
        }
        kualEscala.setValue(laEscala);
        notaFractal.setValue(Integer.toString(laNotaGenerada));
        //notaMidi.setValue(null);
        escalas.forwardChain();
        resultado = notaMidi.getValue();
        System.out.print("Nota escalada: " + resultado + "\n");
        notaEnEscala = Integer.parseInt(resultado);
        return notaEnEscala;
     }
